)    //{    //    $CUSTPartyName                       = $row->PartyName;        //$CUSTfast_client_id                = $row->fast_client_id;        ///////// TBD: verificare client    //    $CUSTPartyTaxSchemeCompanyID         = $row->PartyTaxSchemeCompanyID;    //    $CUSTPartyTaxSchemeTaxScheme         = $row->PartyTaxSchemeTaxScheme;    //    $CUSTPostalAddressStreetName         = $row->PostalAddressStreetName;    //    $CUSTPostalAddressCityName           = $row->PostalAddressCityName;        //$CUSTPostalAddressPostalZone         = $row->PostalAddressPostalZone;    //    $CUSTPostalAddressCountrySubentity   = $row->PostalAddressCountrySubentity;    //    $CUSTPostalAddressCountry            = $row->PostalAddressCountry;    //    $CUSTPartyLegalEntityRegistrationName= $row->PartyLegalEntityRegistrationName;    //    $CUSTPartyLegalEntityCompanyID       = $row->PartyLegalEntityCompanyID;    //}    $cust->getCustomerParty();}class SupplierParty{    public $SUPPartyID;    public $SUPPartyName;    public $SUPPlatitorTVA;    public $SUPPartyTaxSchemeCompanyID;    public $SUPPartyTaxSchemeTaxScheme;    public $SUPPostalAddressStreetName;    public $SUPPostalAddressCityName;    public $SUPPostalAddressCountrySubentity;    public $SUPPostalAddressCountry;    public $SUPPartyLegalEntityRegistrationName;    public $SUPPartyLegalEntityCompanyID;        public $SUPPaymentMeansCode;    public $SUPPayeeFinancialAccountID;                            function getSupplierParty()    {        global $conn;        if($this->SUPPartyID != "")        {            $sql = "SELECT * FROM fast_supplierparty WHERE fast_id =".$this->SUPPartyID.";";            //print($sql);            $result = $conn -> query($sql);            if ($row = $result ->fetch_object())            {                $this->SUPPartyName                       = $row->PartyName;                //$SUPfast_client_id                = $row->fast_client_id;                ///////// TBD: verificare client                $this->SUPPlatitorTVA                = $row->fast_platitorTVA;                $this->SUPPartyTaxSchemeCompanyID   = $row->PartyTaxSchemeCompanyID;                $this->SUPPartyTaxSchemeTaxScheme         = $row->PartyTaxSchemeTaxScheme;                $this->SUPPostalAddressStreetName         = $row->PostalAddressStreetName;                $this->SUPPostalAddressCityName           = $row->PostalAddressCityName;                //$this->SUPPostalAddressPostalZone         = $row->PostalAddressPostalZone;                $this->SUPPostalAddressCountrySubentity   = $row->PostalAddressCountrySubentity;                $this->SUPPostalAddressCountry            = $row->PostalAddressCountry;                $this->SUPPartyLegalEntityRegistrationName= $row->PartyLegalEntityRegistrationName;                $this->SUPPartyLegalEntityCompanyID       = $row->PartyLegalEntityCompanyID;                //$this->SUPCompanyLegalForm              = $row->CompanyLegalForm;                //$this->SUPElectronicMail                = $row->ElectronicMail;            }                        $sql = "SELECT * FROM fast_supplier_paymentmeans WHERE fast_supplier_id =".$this->SUPPartyID.";";            //print($sql);            $result = $conn -> query($sql);            if ($row = $result ->fetch_object())            {                $this->SUPPayeeFinancialAccountID = $row->PayeeFinancialAccountID;                $this->SUPPaymentMeansCode        = $row->PaymentMeansCode;            }        }        else        {            $this->SUPPartyName                       = "";            //$this->SUPfast_client_id                = "";            ///////// TBD: verificare client            $this->SUPPlatitorTVA                     = 1;            $this->SUPPartyTaxSchemeCompanyID         = "";            $this->SUPPartyTaxSchemeTaxScheme         = "";            $this->SUPPostalAddressStreetName         = "";            $this->SUPPostalAddressCityName           = "";            //$this->SUPPostalAddressPostalZone         = "";            $this->SUPPostalAddressCountrySubentity   = "";            $this->SUPPostalAddressCountry            = "";            $this->SUPPartyLegalEntityRegistrationName= "";            $this->SUPPartyLegalEntityCompanyID       = "";            //$this->SUPCompanyLegalForm              = "";            //$this->SUPElectronicMail                = "";                        $this->SUPPayeeFinancialAccountID = "";             $this->SUPPaymentMeansCode        = "";        }    }}class CustomerParty{    public $CUSTPartyID;    public $CUSTPartyName;    public $CUSTPartyTaxSchemeCompanyID;    public $CUSTPartyTaxSchemeTaxScheme;    public $CUSTPostalAddressStreetName;    public $CUSTPostalAddressCityName;    //public $CUSTPostalAddressPostalZone;    public $CUSTPostalAddressCountrySubentity;    public $CUSTPostalAddressCountry;    public $CUSTPartyLegalEntityRegistrationName;    public $CUSTPartyLegalEntityCompanyID;                                function getCustomerParty()    {        global $conn;        if($this->CUSTPartyID != "")        {            $sql = "SELECT * FROM fast_customerparty WHERE fast_id =".$this->CUSTPartyID.";";            //print($sql);            $result = $conn -> query($sql);            if ($row = $result ->fetch_object())            {                $this->CUSTPartyName                       = $row->PartyName;                //$this->CUSTfast_client_id                = $row->fast_client_id;                ///////// TBD: verificare client                $this->CUSTPartyTaxSchemeCompanyID         = $row->PartyTaxSchemeCompanyID;                $this->CUSTPartyTaxSchemeTaxScheme         = $row->PartyTaxSchemeTaxScheme;                $this->CUSTPostalAddressStreetName         = $row->PostalAddressStreetName;                $this->CUSTPostalAddressCityName           = $row->PostalAddressCityName;                //$this->CUSTPostalAddressPostalZone         = $row->PostalAddressPostalZone;                $this->CUSTPostalAddressCountrySubentity   = $row->PostalAddressCountrySubentity;                $this->CUSTPostalAddressCountry            = $row->PostalAddressCountry;                $this->CUSTPartyLegalEntityRegistrationName= $row->PartyLegalEntityRegistrationName;                $this->CUSTPartyLegalEntityCompanyID       = $row->PartyLegalEntityCompanyID;            }        }        else        {            $this->CUSTPartyName                       = "";            //$this->CUSTfast_client_id                = $row->fast_client_id;            ///////// TBD: verificare client            $this->CUSTPartyTaxSchemeCompanyID         = "";            $this->CUSTPartyTaxSchemeTaxScheme         = "";            $this->CUSTPostalAddressStreetName         = "";            $this->CUSTPostalAddressCityName           = "";            //$this->CUSTPostalAddressPostalZone         = "";            $this->CUSTPostalAddressCountrySubentity   = "";            $this->CUSTPostalAddressCountry            = "";            $this->CUSTPartyLegalEntityRegistrationName= "";            $this->CUSTPartyLegalEntityCompanyID       = "";        }    }}?><html lang="RO"><head><meta name="viewport" content="width=device-width, initial-scale=1.0"><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><title>Generare XML pentru sistemul e-factura</title><meta name="description" content="Generare rapidÄƒ UBL pentru sistemul e-factura"/><!--link rel="shortcut icon" href="./favicon.ico" /--><link name="author" content="dataseed.info" /><link rel="stylesheet" type="text/css" href="./styles.css"><script type="text/javascript" src="./scripturi.js"></script><script language=javascript>function Save(){    if(document.frmForm.ID.value === "")    //if(document.getElementsByName("ID").value === undefined)    {        alert("EROARE! CompletaÈ›i numÄƒrul facturii!");        document.frmForm.ID.focus();        return;    }    if(document.frmForm.IssueDate.value === "")    {        alert("EROARE! CompletaÈ›i data facturii!");        document.frmForm.IssueDate.focus();        return;    }    if(document.frmForm.SUPPayeeFinancialAccountID.value === "")    {        alert("EROARE! CompletaÈ›i IBAN-ul furnizorului!");        document.frmForm.SUPPayeeFinancialAccountID.focus();        return;    }        document.frmForm.hdnFor.value = "SAVE";    document.frmForm.submit();}function AddNew(){    //document.frmForm..value = "";    //document.getElementById("frmForm").reset();    var elements = frmForm.elements;    for(i=0; i<elements.length; i++)     {        field_type = elements[i].type.toLowerCase();        switch(field_type)        {            case "text":            case "number":            case "textarea":            case "hidden":                elements[i].value = "";                break;            case "select-one":            case "select-multi":                elements[i].selectedIndex = -1;                break;            default:                  break;        }    }    //alert(window.location.href);    //alert('fast_id=1');    //txt = window.location.href;    //res = txt.replace('fast_id=1', 'fast_id=');    //alert(res);    //document.frmForm.action = res;    //document.frmForm.reset();    document.frmForm.hdnFor.value = "ADDNEW";    document.frmForm.submit();}function item(i){    //alert(i);    document.frmForm.ID.value = '<?php print($fast_invoice_id) ?>';    document.frmForm.method = "post";    document.frmForm.action = "item.php";    if(i === "" || i === undefined)    {        document.frmForm.hdnFor.value = "ADDNEW";    }    else    {        document.frmForm.fast_item_id.value = i;        document.frmForm.hdnFor.value = "EDIT";    }    //alert(document.frmForm.hdnFor.value);    document.frmForm.submit();}function sup